DROP TABLE IF EXISTS `t_customer`;
CREATE TABLE `t_customer` (
  `c_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '客户ID',
  `e_id` int(11) NOT NULL DEFAULT '0' COMMENT '员工ID',
  `c_date` date NOT NULL COMMENT '日期',
  `c_situation` longtext COMMENT '客户情况',
  `c_channel` longtext COMMENT '客户来源渠道',
  `c_psychology` longtext COMMENT '客户购琴心里',
  `c_characteristics` longtext COMMENT '客户分类与特点',
  `c_evaluation` longtext COMMENT '客户评价',
  `c_promotion` longtext COMMENT '营销与宣传方式',
  `c_advice` longtext COMMENT '改进建议',
  `create_date` datetime DEFAULT NULL COMMENT '创建日期',
  `update_date` datetime DEFAULT NULL COMMENT '修改日期',
  `create_user` varchar(255) DEFAULT NULL COMMENT '创建人',
  `update_user` varchar(255) DEFAULT NULL COMMENT '修改人',
  PRIMARY KEY (`c_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='客户信息记录表';

